export default function library(state ={authors:[], books:[]}, action) {
    switch(action.type) {
        case "changeAuthors": {
            state = {...state, authors:action.payload}
            break;
        }
        case 'changeBooks': {
            state = {...state , books:action.payload}
            break;
        }
        default : {

        }
    }
    return state;
}
