import Author from './Author'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'
import React, { Component } from 'react'
import {showAuthors, deleteAuthor} from '../actions/libraryAction'

class Authors extends Component {
    constructor (props) {
        super(props.Book);
        this.deleteAuthor = this.deleteAuthor.bind(this);
    }
    componentDidMount () {
        console.log('Authors=', this.props.library.authors);
        this.props.dispatch(showAuthors());
    }
    deleteAuthor = async (id) => {
        const currentAuthor = this.props.library.authors;
        const authors = await deleteAuthor(id, currentAuthor);
        this.props.dispatch({type:'changeAuthors', payload:authors})
    }
    render() {
        const Authors = this.props.library.authors;
        return (
            <div>
                <table className="table container">
                    <thead className="thead-dark">
                        <tr>
                            <th>AuthorId</th>
                            <th>AuthorImage</th>
                            <th>Content</th>
                            <th>Title</th>
                            <th>Update</th>
                            <th>Delete</th>
                            <th><Link to='/addAuthor'>Add Author</Link></th>
                        </tr>
                    </thead>
                    <thead>
                        {Authors.map((items, index) => (
                        <Author author={items} key={index} deleteAuthor = {this.deleteAuthor}  />
                    ))}
                    </thead>
                </table>
            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        library: state.library
    };
}

export default connect(mapStateToProps)(Authors);
