import Book from './Book'
import { Link } from 'react-router-dom'
import { connect } from 'react-redux'

import { showBooks , deleteBook} from '../actions/libraryAction'


import React, { Component } from 'react'

class Books extends Component {
    constructor (props) {
        super(props);
        this.deleteBook = this.deleteBook.bind(this);
    }
    componentDidMount() {
        this.props.dispatch(showBooks());
    }
    deleteBook = async (id) => {
        const currentBooks =  JSON.parse(JSON.stringify(this.props.library.books))
        const books = await deleteBook(id, currentBooks)
        this.props.dispatch({type:'changeBooks', payload:books})
    }
    render() {
        const books = this.props.library.books;
        return (
            <div>
                <table className="table container">
                    <thead className="thead-dark">
                    <tr>
                        <th scope="col">Id</th>
                        <th scope="col">Author </th>
                        <th scope="col">BookName</th>
                        <th scope="col">Content</th>
                        <th scope="col">Image</th>
                        <th scope="col">ISBN</th>
                        <th scope="col">AuthorId</th>
                        <th scope="col">UPDATE</th>
                        <th scope="col">DELETE</th>
                        <th><Link to='/AddBook'>Add Book</Link></th>
                    </tr>
                    </thead>
                    <thead>
                    {books.map((items, index) => (
                        <Book items={items} key={index} deleteBook = {this.deleteBook} />
                    ))}
                    </thead>
                </table>

            </div>
        )
    }
}

const mapStateToProps = (state) => {
    return {
        library: state.library,
    };
}

export default connect(mapStateToProps)(Books);
